sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

    var ga = new GlideAjax('EmailVerificationAJAX');
    ga.addParam('sysparm_name', 'verifyEmail');
    ga.addParam('sysparm_email', newValue);
    ga.getXML(validate);

   function validate(response) {

      var answer = response.responseXML.documentElement.getAttribute("answer");
      if (answer == 'false') {
        g_form.addErrorMessage('Incorrect email. Correct email should be like this: example@example.com');

      } else {
           g_form.clearMessages();
      }

}

}
